mapper.select:
select a.*, m.nickname as writerName from article as a inner join `member` as m on a.memberId = m.id where 1 = 1
AND a.boardId = #{boardId}
order by a.id desc limit #{limitFrom}, #{itemsInPage};

mapper.update:
UPDATE article

    updateDate = now(),

        title = #{title},


        body = #{body}


WHERE id = #{id}
